from Testing import ZopeTestCase# Make the boring stuff load quietlyZopeTestCase.installProduct('ATExtensions')ZopeTestCase.installProduct('CMFBibliographyAT')ZopeTestCase.installProduct('ATBiblioStyles')ZopeTestCase.installProduct('ATBiblioList')from Products.PloneTestCase import PloneTestCaseproducts=(    'ATExtensions',    'CMFBibliographyAT',    'ATBiblioStyles',    'ATBiblioList',    )PloneTestCase.setupPloneSite(products=products)class BibliolistTestCase(PloneTestCase.PloneTestCase):    class Session(dict):        def set(self, key, value):            self[key] = value    def _setup(self):        PloneTestCase.PloneTestCase._setup(self)        self.app.REQUEST['SESSION'] = self.Session()class BibliolistFunctionalTestCase(PloneTestCase.FunctionalTestCase):        class Session(dict):        def set(self, key, value):            self[key] = value    def _setup(self):        PloneTestCase.FunctionalTestCase._setup(self)        self.app.REQUEST['SESSION'] = self.Session()